import React from "react";
import '../style.css';
import { connect } from "react-redux";


const mapStateToProps = (state) => {
    return {
        items: state.items
    }
}

const Cards = (props) => {
    const addCard = (card) => {
        props.dispatch({ type: "ADD_PROJECT", data: card })
    }

    return (
        <div>
            <div>Projects</div>
            <div>
                {props.card.map(
                    i => <div key={i.name}>
                        <button onClick={() => addCard(i)}>Add Project</button>
                    </div>
                )}
            </div>
        </div>
    )
}
