// 
const intialState = {
    project: [
        { name: "Name", description: "Description" }
    ],
    addProject: []
}

const mainFunction = (state = intialState, action) => {
    switch (action.type) {
        case "ADD_PROJECT":
            return {
                ...state,
                addProject: [...state.addProject, action.data]
            }
        default: return state;
    }
}
// 

export default mainFunction;
