import '../style.css';
import AWS from '../documents/AWS_Academy_Graduate___AWS_Academy_Data_Center_Technician_Badge20220622-46-142d061.svg';

const Certificates = () => {

    const certificates = [
        {name: "AWS Data Center Technician"},
        {description: "This certificate was awareded to me...."},
        {from: "Amazon Web Services"}
    ]

    return (
        <div className="certificates">

            <div className='grid-container'>
                {
                    certificates?.map((data, i) => (
                        <div key={i}>
                            {data.name}
                            {data.description}
                            {data.from}
                        </div>
                    ))
                }
            </div>
            
        </div>
    )
}

export default Certificates;
