import React from "react";
import '../style.css';
import { connect } from "react-redux";
import Cards from "./Cards";

// 
const intialState = {
    project: [
        { name: "Name", description: "Description" }
    ],
    addProject: []
}

export const mainFunction = (state = intialState, action) => {
    switch (action.type) {
        case "ADD_PROJECT":
            return {
                ...state,
                addProject: [...state.cart, action.data]
            }
        default: return state;
    }
}
// 



const Projects = () => {


    return (
        <div>
            <div>Projects</div>
            <Cards />
        </div>
    )
}

export default connect(mapStateToProps)(Projects);
