import React from "react";
import '../style.css';
import { connect } from "react-redux";

// 
const intialState = {
    project: [
        { name: "Name", description: "Description" }
    ],
    addProject: []
}

export const mainFunction = (state = intialState, action) => {
    switch (action.type) {
        case "ADD_PROJECT":
            return {
                ...state,
                addProject: [...state.cart, action.data]
            }
        default: return state;
    }
}
// 

const mapStateToProps = (state) => {
    return {
        items: state.items
    }
}

export const Cards = (props) => {
    const addCard = (card) => {
        props.dispatch({ type: "ADD_PROJECT", data: card })
    }
}

const Projects = () => {


    return (
        <div>
            <div>Projects</div>
            <div>
                {props.card.map(
                    i => <div key={i.name}>
                        <button onClick={() => addCard(i)}>Add Project</button>
                    </div>
                )}
            </div>
        </div>
    )
}

export default connect(mapStateToProps)(Projects);
